mapper.select:
select

from student


    id= #{id,jdbcType=INTEGER}
    and


    name=#{username,jdbcType=VARCHAR}
    and


    gender=#{gender,jdbcType=INTEGER}
    and


    registerNo=  #{age,jdbcType=VARCHAR}
    and


    phone=  #{age,jdbcType=VARCHAR}
    and


    role=  #{age,jdbcType=VARCHAR}
    and


    status=  #{age,jdbcType=INTEGER}
    and


    createTime=#{createTime,jdbcType=TIMESTAMP}
    and


    updateTime= #{updateTime,jdbcType=TIMESTAMP}
    and
